mapper.select:
SELECT count(*) 
FROM SWIPE.CSR_SWIPECARDTIME
WHERE EMP_ID = #{EMP_ID}
  AND SwipeCardTime is not NULL
  AND Shift = #{Shift}
  AND  SwipeCardTime >  to_date(to_char(sysdate,'yyyy-MM-dd'),'yyyy-MM-dd') and SwipeCardTime < to_date(to_char(sysdate+1,'yyyy-MM-dd'),'yyyy-MM-dd')

select count(*) from SWIPE.CSR_SWIPECARDTIME where EMP_ID = #{EMP_ID} 
AND Shift = #{Shift}
AND SwipeCardTime2 is not NULL 
and  SwipeCardTime > to_date(to_char(sysdate,'yyyy-MM-dd'),'yyyy-MM-dd') and SwipeCardTime < to_date(to_char(sysdate+1,'yyyy-MM-dd'),'yyyy-MM-dd')

select count(*) from SWIPE.CSR_SWIPECARDTIME where EMP_ID = #{EMP_ID} 
AND Shift = #{Shift}
AND SwipeCardTime2 is not NULL 
and  SwipeCardTime > to_date(to_char(sysdate-1,'yyyy-MM-dd'),'yyyy-MM-dd') and SwipeCardTime < to_date(to_char(sysdate,'yyyy-MM-dd'),'yyyy-MM-dd')

select count(*) from SWIPE.CSR_SWIPECARDTIME where EMP_ID = #{EMP_ID} 
AND Shift = #{Shift}
AND SwipeCardTime is not NULL 
and  SwipeCardTime > to_date(to_char(sysdate-1,'yyyy-MM-dd'),'yyyy-MM-dd') and SwipeCardTime < to_date(to_char(sysdate,'yyyy-MM-dd'),'yyyy-MM-dd')

select count(*)  from SWIPE.CSR_SWIPECARDTIME where EMP_ID = #{EMP_ID} 
AND Shift = #{Shift}
AND SwipeCardTime is NULL and SwipeCardTime2 is not null
and  SwipeCardTime2 > to_date(to_char(sysdate,'yyyy-MM-dd'),'yyyy-MM-dd') and SwipeCardTime2 < to_date(to_char(sysdate+1,'yyyy-MM-dd'),'yyyy-MM-dd')

mapper.update:
    	update SWIPE.CSR_SWIPECARDTIME set SwipeCardTime2 = #{SwipeCardTime2} 
    	WHERE EMP_ID = #{EMP_ID} 
    	and Shift = #{Shift} 
		and  to_char(SwipeCardTime,'yyyy-MM-dd') = to_char(sysdate,'yyyy-MM-dd')

    	update SWIPE.CSR_SWIPECARDTIME set SwipeCardTime2 = #{SwipeCardTime2} 
    	WHERE EMP_ID = #{EMP_ID} 
    	and Shift = #{Shift} 
		and  to_char(SwipeCardTime,'yyyy-MM-dd') = to_char(#{SwipeCardTime2}-interval '24' hour,'yyyy-MM-dd')

    	update SWIPE.CSR_SWIPECARDTIME set SwipeCardTime2 = #{SwipeCardTime2}
    	WHERE  WorkshopNo=#{WorkshopNo}
		  and  SwipeCardTime >  to_date(to_char(sysdate,'yyyy-MM-dd'),'yyyy-MM-dd')

UPDATE SWIPE.CSR_SWIPECARDTIME
SET RC_NO = #{RC_NO},
PRIMARY_ITEM_NO=#{PRIMARY_ITEM_NO}
WHERE
EMP_ID=#{EMP_ID} 
AND   swipecardtime >to_date(to_char(sysdate,'yyyy-MM-dd'),'yyyy-MM-dd')

	UPDATE SWIPE.CSR_SWIPECARDTIME
	SET RC_NO = #{RC_NO},
	PRIMARY_ITEM_NO=#{PRIMARY_ITEM_NO}
	WHERE	
	WorkshopNo=#{WorkshopNo} 
    and  to_char(SwipeCardTime,'yyyy-MM-dd') = to_char(sysdate-interval '12' hour,'yyyy-MM-dd')

mapper.select:
select count(*)  from SWIPE.CSR_SWIPECARDTIME where EMP_ID = #{EMP_ID} 
AND WorkshopNo = #{WorkshopNo}
 AND SwipeCardTime2 is NULL 
and  swipecardtime >to_date(to_char(sysdate,'yyyy-MM-dd'),'yyyy-MM-dd') and SwipeCardTime < to_date(to_char(sysdate+1,'yyyy-MM-dd'),'yyyy-MM-dd')

mapper.update:
    	update SWIPE.raw_record set record_status = #{Record_Status}
    	WHERE CardID=#{CardID}
		and to_char(swipecardtime,'yyyy-MM-dd HH24:mi:ss') =to_char(#{SwipeCardTime},'yyyy-MM-dd HH24:mi:ss')

		    and ID = #{Id}


		    and ID is null

mapper.select:
    	SELECT com_ip 
		FROM SWIPE.app_login_control WHERE
 		control_except = #{control_except}

select count(*)
from swipe.csr_swipecardtime
where WorkshopNo=#{WorkshopNo}
and PROD_LINE_CODE=#{ProdLineCode} 
and  swipe_date=to_char(sysdate,'yyyy-mm-dd')

  		select LineSize
  		from swipe.LINENO
  		where WorkshopNo=#{WorkShopNo}

		    and LINENO=#{LineNo}

mapper.update:
    	update SWIPE.CSR_SWIPECARDTIME set WORKSHOPNO=#{WorkshopNo},PROD_LINE_CODE = #{ProdLineCode}
    	WHERE EMP_ID=#{EMP_ID}
    	AND SWIPECARDTIME2 IS NULL 
    	AND SWIPECARDTIME IS NOT NULL 
		AND SWIPE_DATE = to_char(sysdate,'yyyy-mm-dd')

    	update SWIPE.CSR_SWIPECARDTIME set WORKSHOPNO=#{WorkshopNo},PROD_LINE_CODE = #{ProdLineCode}
    	WHERE EMP_ID=#{EMP_ID}
    	AND SWIPECARDTIME2 IS NULL 
    	AND SWIPECARDTIME IS NOT NULL 
		AND (SWIPE_DATE = to_char(sysdate,'yyyy-mm-dd')
		or (SWIPE_DATE = to_char(sysdate-1,'yyyy-mm-dd') and shift='N'))
